const initialState = {
    loan: false,
    loanAmount: 0
}

const loanReducer = (state = initialState, action) => {
    switch(action.type){
        case "APPLY":
            return { loan: true }        
        case "SET":
            return { loanloanAmount : action.payload }    
        default: 
        return state;
    }
}

export default loanReducer;
