import { useDispatch } from 'react-redux'

const dispatch  = useDispatch()

export function deposit(){
    return { type: "DEPOSIT", payload: 100}
}

export function loading(){
    return {type: 'LOADING'}
}

export function asyncDeposit(){
    return dispatch => {
        setTimeout(() =>{
            dispatch(deposit)
        }, 1000)
        dispatch(loading())
    }
}
