export function deposit(){
    return { type: "DEPOSIT", payload: 100}
}

export function withdraw(){
    return { type: "WITHDRAW", payload: 100}
}

export function loading(){
    return {type: 'LOADING'}
}

export function asyncDeposit(){
    return dispatch => {
        setTimeout(() =>{
            dispatch(deposit())
        }, 1000)
        dispatch(loading())
    }
}

export function asyncWithdraw(){
    return dispatch => {
        setTimeout(() =>{
            dispatch(withdraw())
        }, 1000)
        dispatch(loading())
    }
}
