import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'

export default function Home() {
  const [loanAmount, setLoanAmount] = useState(10000)

  const balance = useSelector(state => {
    console.log({state})
    return state.balanceReducer.balance
})

const loan = useSelector(state => state.loanReducer.loan)

const loanTotal = useSelector(state => state.loanReducer.loanAmount)
const dispatch = useDispatch()

const onLoan = () => {
  dispatch({
    type: "APPLY",
    payload: loanAmount
  })
}

function handleClick(e){
  e.prevetDefault()
  setLoanAmount(e.target.value)
}

  return (
      <div>
       <h3>Deposit: { balance }   </h3> 
       <form >
        <label> Amount of Loan to apply for </label>
        <input 
        type="number" 
        // onChange={(e) => setLoanAmount(e.target.value)} 
        onChange={(e) => setLoanAmount(e)} 
        value={loanAmount}
      />    
      <button onClick={onLoan}> Click for money</button>
       </form>
    your loan is for {loanTotal}
       <h3> Loan: {loan && "loan approved"}</h3>
    </div>

  )
}
