const initialState = {
    balance: 1
}

const balance = (state = initialState, action) => {
    switch(action.type){
        case "DEPOSIT":
            return { balance: state.balance + action.payload }        
        case "WITHDRAW":
        return { balance: state.balance - action.payload }        
        default: 
        return state;
    }
}

export default balance;
